mapper.select:
select

from order_item


    AND id = #{id,jdbcType=INTEGER}


    AND project_id = #{projectId}


     AND total_Num >= #{totalNum}


     AND  total_Num < #{totalNumUp}


     AND project_cost >= #{projectCost}


     AND  project_cost < #{endPrice}


     AND acture_start_tm >= #{actureStartTm}


     AND  end_time <= #{endTime}


     AND  fee_tags = #{feeTags}


    AND acture_start_tm like #{actureStartTm}"%"

mapper.update:
update order_item


    order_status = #{status},

  update_time = now()

where id in (

  #{orderIds.id}

)
